graphql.query:
query ShipmentDetails {
    shipmentDetails(
        reference: "SampleReference"
        containerNumber: "SampleContainerNumber"
    ) {
        shipmentDetailsURL
        shipmentStatus
        reference
        unitId
        stops {
            stopNumber
            locationType
            location
            city
            country
            latitude
            longitude
            milestones {
                eventCode
                eventName
                estimatedDate
                actualDate
                plannedDate
            }
        }

    }
}
